mapper.update:
update t_key_source_config
set priority = priority - 1
where priority <= #{newPriority}
and priority > #{oldPriority}

update t_key_source_config
set priority = priority + 1
where priority >= #{newPriority}
and priority < #{oldPriority};

update t_key_source_config
set priority = priority + 1
where priority >= #{priority}
and priority != 5;

update t_key_source_config
set priority = priority - 1
where priority > #{priority}
and priority != 5;

update t_key_source_config
set source_ip = #{sourceIp},
source_ip2 = #{sourceIp2}
where id = #{id} ;

update t_key_source_config
set priority = #{priority}
where id = #{id} ;
