mapper.update:
update t_key_info
set key_status = #{keyStatus}
where key_id = #{keyId}

mapper.select:
select * from t_key_info
where binary applicant = #{applicant}
and key_status = #{keyStatus}
order by update_time desc
limit 50

select * from t_key_info
where binary applicant = #{applicant}
and key_status != #{keyStatus}
order by update_time desc
limit 50

select count(1) from t_key_info
where applicant = #{applicant}
and key_status = 2
